const apiURl = 'https://api.giphy.com/v1/gifs/search?api_key=vhtnPW6GjeP1njrRj0qUvJsmXXEnLNLS&q=panda&limit=10&offset=0&rating=g&lang=en'

export default function getGifs({keyword}){

    return fetch(apiURl)
    .then(res => res.json())
    .then(response => {
      const {data} = response;
      const gifs = data.map (image => image.images.downsized_medium.url);
      return gifs;
    })


}
