import React, {useEffect, useState} from 'react';
import './App.css';
import getGifs from './services/getGifs';
import Gif from './components/Gif'

function App()  {

  const [gifs, setGifs] = useState([]);

  // La primera hace el renderizado del componente, solo se ejecuta una vez gracias al [] sin dependencias
  useEffect(function(){
    getGifs({keyword: 'futbol'}).then(gifs => setGifs(gifs));
  },[]) 

  return (
    <div className="App">
      <section className ="App-content">
         { 
           /* Hacemos un mapeo para renderizar una imagen haces un mapeo del array*/ 
            gifs.map(singleGif =>
              <Gif title ={singleGif.title}  url={singleGif.url} id={singleGif.id}/>
            )
          }
      </section>
    </div>
  );
}


export default App;
