import React, {useEffect, useState} from 'react';
import './App.css';


const apiURl = 'https://api.giphy.com/v1/gifs/search?api_key=vhtnPW6GjeP1njrRj0qUvJsmXXEnLNLS&q=panda&limit=10&offset=0&rating=g&lang=en'

function App()  {

  const [gifs, setGifs] = useState([]);

  // La primera hace el renderizado del componente, solo se ejecuta una vez gracias al [] sin dependencias
  useEffect(function(){
    console.log('Efecto ejecutado');

    fetch(apiURl)
      .then(res => res.json())
      .then(response => {
        const {data} = response;
        const gifs = data.map (image => image.images.downsized_medium.url);
        console.log(gifs);
      })
  },[]) 

  return (
    <div className="App">
      <section className ="App-content">
        {
          /* Hacemos un mapeo para renderizar una imagen haces un mapeo del array*/ 
          gifs.map(singleGif => <img src={singleGif} />)

        }

        <button onClick= {() => setGifs()}> Cambiar GIFS</button>

      </section>
    </div>
  );
}


export default App;
