import React, {useEffect, useState} from 'react';
import './App.css';
import getGifs from './services/getGifs';

function App()  {

  const [gifs, setGifs] = useState([]);

  // La primera hace el renderizado del componente, solo se ejecuta una vez gracias al [] sin dependencias
  useEffect(function(){
    getGifs().then(gifs => setGifs(gifs));
  },[]) 

  return (
    <div className="App">
      <section className ="App-content">

          /* Hacemos un mapeo para renderizar una imagen haces un mapeo del array*/ 
         { 
          gifs.map(singleGif =>{
            return <div>
              <h4>{singleGif.title}</h4>
              <small> {singleGif.id}</small>
              <img src={singleGif.url} />

            </div>

          })
          }


      </section>
    </div>
  );
}


export default App;
